
public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String x= "water";
		String y = "cool-aid";
		String temp;
		System.out.println(x);
        System.out.println(y);
		temp=x;
		x=y;
		y=temp;
        System.out.println(x);
        System.out.println(y);
	}

}
